import React, { useEffect, useState } from "react";
import { useDisplay } from "../Provider";
import style from "../styles/controll.module.css";
import ZingTouch from "zingtouch";

export default function Controller() {
  const [originD , setOriginD]=useState(0);
  const [lastD , setLastD]=useState(0);
  const [angle , setAngle]=useState(0);
  useEffect(() => {
    var zt = new ZingTouch.Region(document.body);
    var myElement = document.getElementById("Controller");

    zt.bind( myElement, "rotate",
      function (e) {
        // console.log(e.detail)
        // setOriginD(e.detail.distanceFromOrigin);
        // setLastD(e.detail.distanceFromLast);
        // setAngle(e.detail.angle);
        const orignd=(e.detail.distanceFromOrigin);
        if(orignd -originD >30){
          setOriginD(orignd);
          console.log( "originD", originD);
        }
        // console.log( e.detail.angle);
      },
      false
    );

   
  });

  const display = useDisplay();
  return (
    <div id="Controller">
      <div className={style.contollcontainer}>
        <div className={style.row}>
          <img src="#" />
          <img src="#" />
        </div>
        <div className={style.column}>
          <button onClick={display.goTomenu}>M</button>
          <button onClick={display.goBack}>B </button>
        </div>
        <button className={style.select}>S </button>
      </div>
    </div>
  );
}
