import {  useState , useContext } from "react";
import {DisplayContext} from './displayProvider';

export const useDisplay=()=>{
      return useContext(DisplayContext);
}
export default DisplayState = () => {

  const [history, setHistory] = useState([1]);
  const [index, setIndex] = useState(243);

  const Changedisplay = (vlaue) => {
    setHistory(...history, vlaue);
    setIndex(vlaue);
  };

  const goBack = () => {
    const historylen = history.length;
    setIndex(history[historylen-1]);
    const newhistory = history.splice(0, historylen - 2);
    setHistory(newhistory);
  };
  const goTomenu=()=>{
    setIndex(0);
    setHistory([]);
  }

  return {
     goBack , index, goTomenu , Changedisplay
  };
};
