  const [limit  , setLimit]=useState(0);
  const [active , setActive] =useState(2);

  const Changedisplay = (value , historyindex) => {
    const newHistory=history;
    newHistory.push(historyindex);
    setHistory(newHistory);
    setIndex(value);
  };

  const goBack = () => {
    console.log("going back")
    if(history.length>0 && history.length-1 >=0){
      setHistory( history.slice(0, history.length - 1));
        setIndex(history[history.length-1])
  }
   
  };
  const goTomenu=()=>{
    setIndex(0);
    setHistory([]);
  }
  const changeLimit=(value)=>{
     setLimit(value);
  }
  const changeActiveUp=()=>{
     if(active<=limit-1){
      setActive(active+1)
     }
     else{setActive(0)}
  }
  const changeActiveDown=()=>{
    if(active>0){
     setActive(active-1);
    }
    else{setActive(0)}

 }
  return {
     goBack , index, goTomenu , Changedisplay , changeLimit , limit , active
  };
};
export default DisplayState;
