import {  useState , useContext } from "react";
import {DisplayContext} from './displayProvider';

export const useDisplay=()=>{
      return useContext(DisplayContext);
}
const DisplayState = () => {

  const [history, setHistory] = useState([1]);
  const [index, setIndex] = useState(0);

  const Changedisplay = (value , historyindex) => {
    const newHistory=history;
    newHistory.push(historyindex);
    setHistory(newHistory);
    setIndex(value);
  };

  const goBack = () => {
    console.log("going back")
    if(history.length>0 && history.length-2 >=0){
      setHistory( history.slice(0, history.length - 1));
        setIndex(history[history.length-1])
  }
   
  };
  const goTomenu=()=>{
    setIndex(0);
    setHistory([]);
  }

  return {
     goBack , index, goTomenu , Changedisplay
  };
};
export default DisplayState;
