import React, { useEffect, useRef, useState } from "react";
import { useDisplay } from "../Provider";
import style from "../styles/controll.module.css";
import ZingTouch from "zingtouch";

 const Controller=() =>{
   const [distance , setDistance]=useState(0);
  //  const distance=useRef(0);
  useEffect(() => {
    var zt = new ZingTouch.Region(document.body);
    var myElement = document.getElementById("Controller");

    zt.bind( myElement, "rotate",
      function (e) { 
         const value=(e.detail.distanceFromOrigin);
  
        //  console.log("value" , value , "distance" , distance);
         if(value-distance >50){
          console.log("up");
          // setDistance(value-distance);
          // distance.current=value
          setDistance(value)
         }
         else if(value-distance <50){
          console.log("down");
          // distance.current=value;
          setDistance(value)
         }
      
      },
      false ,  
    );
    const unbind=()=>{
     return ()=> zt.unbind(myElement, 'rotate');
    }
   
  });

  const display = useDisplay();
  return (
    <div id="Controller"  draggable={false} >
      <div className={style.contollcontainer}>
        <div className={style.row}>
          <img src="#" />
          <img src="#" />
        </div>
        <div className={style.column}>
          <button onClick={display.goTomenu  }>M</button>
          <button onClick={()=>{display.goBack; unbind;}  }>B </button>
        </div>
        <button className={style.select }>S </button>
      </div>
    </div>
  );
}
 export default Controller
