import React, { useEffect, useRef, useState } from "react";
import { useDisplay } from "../Provider";
import style from "../styles/controll.module.css";
import ZingTouch from "zingtouch";

 const Controller=() =>{
  const [direction , setDirection]=useState(0);
  const [isOrigin , setIsOrigin]=useState(0);
  // let direction=0;

  useEffect(() => {
    var zt = new ZingTouch.Region(document.body);
    var myElement = document.getElementById("Controller");
    
    zt.bind( myElement, "rotate",
      function (e) { 
        const angle= Math.floor (e.detail.angle) ;

        const distanceFromLast= Math.floor (e.detail.distanceFromLast) ;

        const distanceFromOrigin=  Math.floor (e.detail.distanceFromOrigin) ;

        if(angle<=180 && angle >=0 ){
        if(angle % 20==0 ){ 
            // console.log("angel " , angle  , "last" ,distanceFromLast  , "origin" , distanceFromOrigin)
            if(distanceFromLast>=0){console.log("up")}
            else{console.log("down")}
            return;

        //   if(direction<angle){
        //     setDirection(angle);
        //     const value= e.detail;
        //     console.log("up")
        //     console.log("angel " , angle  , "last" ,distanceFromLast  , "origin" , distanceFromOrigin)
        //   }
        //  else {
        //     setDirection(angle);
        //     const value= e.detail;
        //     console.log("down")
        //     console.log("angel " , angle  , "last" ,distanceFromLast  , "origin" , distanceFromOrigin)
        //   }
         }
        }
      
        
      },
      true ,  
    );
    // const unbind=()=>{
    //  zt.unbind(myElement, 'rotate');
    // }
   
  });

  const display = useDisplay();
  return (
    <div   >
      <div  id="Controller" className={style.contollcontainer }  draggable={false}>
        <div className={style.row}>
          <img src="#" />
          <img src="#" />
        </div>
        <div className={style.column}>
          <button onClick={display.goTomenu  }>M</button>
          <button onClick={display.goBack  }>B </button>
        </div>
        <button className={style.select }>S </button>
      </div>
    </div>
  );
}
