    const display=useDisplay();
    // console.log(display.Changedisplay(0))
    const data=props.data;
    const index=props.index;
   
  return (
    <button className={"active"} onClick={(e)=>{display.Changedisplay(data.value , index)}}>{data.name}</button>
  )
}
