import React, { useEffect, useRef, useState } from "react";
import { useDisplay } from "../Provider";
import style from "../styles/controll.module.css";
import ZingTouch from "zingtouch";

 const Controller=() =>{
  const [newAngle , setNewAngle]=useState(0);

  useEffect(() => {
    var zt = new ZingTouch.Region(document.body);
    var myElement = document.getElementById("Controller");
    
    zt.bind( myElement, "rotate",
      function (e) { 
        const angle=e.detail.angle;
        const distanceFromLast=Math.abs(e.detail.distanceFromLast);
        if(angle<=180 && angle >=0 ){
          if(distanceFromLast /100 ==0){
            const value= e.detail;
            console.log("val " , value)
          }
          
          
        
        }
      
        
      },
      true ,  
    );
    // const unbind=()=>{
    //  zt.unbind(myElement, 'rotate');
    // }
   
  });

  const display = useDisplay();
  return (
    <div   >
      <div  id="Controller" className={style.contollcontainer }  draggable={false}>
        <div className={style.row}>
          <img src="#" />
          <img src="#" />
        </div>
        <div className={style.column}>
          <button onClick={display.goTomenu  }>M</button>
          <button onClick={display.goBack  }>B </button>
        </div>
        <button className={style.select }>S </button>
      </div>
    </div>
  );
}
 export default Controller
